import { createSlice } from "@reduxjs/toolkit"

const initialCart = {
    quantity: 1,
    totalAmount: 6
}
const cartSlice = createSlice({
    name: 'cart',
    initialState: initialCart,
    reducers: {
        increment(state) {
            state.quantity++;
            state.totalAmount += 6
        },
        decrement(state) {
            state.quantity++;
            state.totalAmount -= 6
        }
    }
})

export const cartActions = cartSlice.actions;

export default cartSlice.reducer;
